INSERT INTO planets (planet_id, planet_title, planet_image, planet_description, albedo, is_delete) 
VALUES
(1, 'Горячий Юпитер', 'hot_jupiter.png', 'Горячие юпитеры — это класс экзопланет, которые похожи на Юпитер по массе и составу, но имеют очень короткие орбиты вокруг своих звезд, что приводит к высоким температурам на их поверхности.', 0.05, false),
(2, 'Углеродная планета', 'carbon_planet.jpg', 'Углеродная экзопланета — тип планеты, в недрах и коре которой преобладает углерод над кислородом. Это миры, где вместо силикатных пород могут быть алмазы, графит и карбиды, а поверхность может быть покрыта углеводородной смолой или сажей.', 0.1, false),
(3, 'Скалистая планета', 'rocky_planet.png', 'Скалистая экзопланета — это тип планеты, которая в основном состоит из твердых материалов, таких как силикатные породы и металлы, подобно Земле, Венере и Марсу в нашей Солнечной системе. Эти планеты имеют твердую поверхность и могут иметь атмосферу.', 0.3, false),
(4, 'Газовый гигант', 'gas_giant.jpg', 'Газовый гигант — это крупная планета, состоящая преимущественно из газов, таких как водород и гелий, с возможным твердым ядром. В нашей Солнечной системе к газовым гигантам относятся Юпитер и Сатурн.', 0.5, false),
(5, 'Мини-Нептун', 'mini_neptun.jpg', 'Мини-Нептун — это тип экзопланеты, которая меньше Нептуна, но больше Земли, обычно с размером от 1,5 до 4 раз больше земного радиуса. Эти планеты имеют толстую атмосферу, богатую водородом и гелием, и могут иметь океаны из воды, аммиака или метана под атмосферой.', 0.4, false),
(6, 'Ледяная планета', 'ice_planet.png', 'Ледяная экзопланета — это тип планеты, которая находится далеко от своей звезды и имеет низкие температуры, что приводит к наличию твердых льдов на поверхности или в атмосфере. Эти планеты могут состоять из водяного льда, аммиачного льда или метанового льда.', 0.6, false),
(7, 'Суперземля', 'super_earth.jpg', 'Суперземля — это тип экзопланеты, которая имеет массу больше земной, но значительно меньше, чем у газовых гигантов, обычно от 1 до 10 земных масс. Эти планеты могут быть скалистыми, ледяными или иметь толстую атмосферу.', 0.35, false),
(8, 'Хтоническая планета', 'chthonic_planet.png', 'Хтоническая планета — это гипотетический тип планеты, которая образовалась из остатков разрушенной планеты или луны, часто в результате столкновения. Эти планеты могут иметь необычный состав и структуру, отличающуюся от традиционных планет.', 0.2, false);